          'x-api-key': 'L5oQfQ6OAmUQfGhdYsaSEEZqShpJBB2hYQg7nCehH9IzgeEX841EBGkRZp648XDz4Osj6vN0BgXvBRHbi6bqreTviFD7xnnXXV7D2N9nEDWMG25S7x31ve1I2W9pzVhA'
        }
      });

      if (!currentStateResponse.ok) {
        return res.status(500).json({ message: "Failed to get current trading state" });
      }

      const currentStateData = await currentStateResponse.json();
      const currentState = currentStateData.state;
      const newState = currentState === 'A' ? 'I' : 'A'; // Toggle: A=Active, I=Inactive

      // Update trading state in CryptoBot API
      const updateStateResponse = await fetch(`https://cryptobot-api-f15f3256ac28.herokuapp.com/account/state?email=${encodeURIComponent(userEmail)}&new_state=${newState}`, {
        method: 'POST',
        headers: {
          'accept': 'application/json',
          'x-api-key': 'L5oQfQ6OAmUQfGhdYsaSEEZqShpJBB2hYQg7nCehH9IzgeEX841EBGkRZp648XDz4Osj6vN0BgXvBRHbi6bqreTviFD7xnnXXV7D2N9nEDWMG25S7x31ve1I2W9pzVhA'
        }
      });

      if (!updateStateResponse.ok) {
        return res.status(500).json({ message: "Failed to update trading state in CryptoBot API" });
      }

      // Log admin action (no local database update needed since we use CryptoBot API as source)
      await storage.logAdminAction({
        adminId: session.adminId.toString(),
        action: 'TOGGLE_TRADING',
        targetUserId: userEmail,
        details: { 
          userEmail: userEmail,
          previousState: currentState,
          newState: newState,
          timestamp: new Date() 
        }
      });

      res.json({ 
        message: `Trading state toggled to ${newState === 'A' ? 'Active' : 'Inactive'}`,
        newState: newState
      });
    } catch (error) {
      console.error("Error toggling trading state:", error);
      res.status(500).json({ message: "Failed to toggle trading state" });
    }
  });

  // Get admin logs
  app.get('/api/admin/logs', isAdminAuthenticated, async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 50;
      const logs = await storage.getRecentAdminLogs(limit);
      res.json(logs);
    } catch (error) {
      console.error("Error fetching admin logs:", error);
      res.status(500).json({ message: "Failed to fetch logs" });
    }
  });

  // Change admin password
  app.post('/api/admin/change-password', isAdminAuthenticated, async (req, res) => {
    try {
      const passwordData = adminChangePasswordSchema.parse(req.body);
      const session = req.session as any;

      const admin = await storage.getAdminByUsername(session.adminUsername);
      if (!admin) {
        return res.status(404).json({ message: "Admin not found" });
      }

      // Verify current password
      const { verifyPassword } = await import("./auth");
      const isCurrentValid = await verifyPassword(passwordData.currentPassword, admin.password);

      if (!isCurrentValid) {
        return res.status(400).json({ message: "Current password is incorrect" });
      }

      // Update password
      await storage.updateAdminPassword(session.adminUsername, passwordData.newPassword);

      // Log admin action
      await storage.logAdminAction({
        adminId: session.adminId.toString(),
        action: 'CHANGE_PASSWORD',
        details: { timestamp: new Date() }
      });

      res.json({ message: "Password changed successfully" });
    } catch (error: any) {
      console.error("Error changing admin password:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: "Invalid password data",
          errors: error.errors 
        });
      }
      res.status(500).json({ message: "Failed to change password" });
    }
  });

  // Get user status from CryptoBot API (admin only)
  app.get('/api/admin/users/:userId/status', isAdminAuthenticated, async (req, res) => {
    try {
      const { userId } = req.params;

      // Since we're now using emails as userIds from CryptoBot API, 
      // userId should be the email address
      const userEmail = userId;

      if (!userEmail) {
        return res.status(400).json({ message: "User email not provided" });
      }

      // Get account state from CryptoBot API
      const accountStateResponse = await fetch(`https://cryptobot-api-f15f3256ac28.herokuapp.com/account/state?email=${encodeURIComponent(userEmail)}`, {
        method: 'GET',
        headers: {
          'accept': 'application/json',
          'x-api-key': 'L5oQfQ6OAmUQfGhdYsaSEEZqShpJBB2hYQg7nCehH9IzgeEX841EBGkRZp648XDz4Osj6vN0BgXvBRHbi6bqreTviFD7xnnXXV7D2N9nEDWMG25S7x31ve1I2W9pzVhA'
        }
      });

      // Get fund amount from CryptoBot API
      const fundResponse = await fetch(`https://cryptobot-api-f15f3256ac28.herokuapp.com/account/fund?email=${encodeURIComponent(userEmail)}`, {
        method: 'GET',
        headers: {
          'accept': 'application/json',
          'x-api-key': 'L5oQfQ6OAmUQfGhdYsaSEEZqShpJBB2hYQg7nCehH9IzgeEX841EBGkRZp648XDz4Osj6vN0BgXvBRHbi6bqreTviFD7xnnXXV7D2N9nEDWMG25S7x31ve1I2W9pzVhA'
        }
      });

      const accountState = accountStateResponse.ok ? await accountStateResponse.json() : null;
      const fundData = fundResponse.ok ? await fundResponse.json() : null;

      res.json({
        userEmail: userEmail,
        userId: userEmail,
        accountState,
        fundData,
        // No local user data needed since we use CryptoBot API as source of truth
        localUser: null
      });
    } catch (error) {
      console.error("Error fetching user status:", error);
      res.status(500).json({ message: "Failed to fetch user status" });
    }
  });

  // Get subscription status for specific user (admin only)
  app.get('/api/admin/users/:userEmail/subscription', isAdminAuthenticated, async (req, res) => {
    try {
      const { userEmail } = req.params;

      // Try to find user in local database by email
      let user = null;
      try {
        user = await storage.getUserByEmail(userEmail);
      } catch (error) {
        // User not found in local database, return default
        return res.json({
          stripeSubscriptionId: null,
          stripeCustomerId: null,
          subscriptionStatus: 'none',
          hasSubscription: false
        });
      }

      if (!user) {
        return res.json({
          stripeSubscriptionId: null,
          stripeCustomerId: null,
          subscriptionStatus: 'none',
          hasSubscription: false
        });
      }

      // If user has subscription, get details from Stripe
      let subscriptionStatus = 'none';
      if (user.stripeSubscriptionId && stripe) {
        try {
          const subscription = await stripe.subscriptions.retrieve(user.stripeSubscriptionId);
          subscriptionStatus = subscription.status;
        } catch (error: any) {
          // Handle test mode subscription IDs in live mode gracefully
          if (error.code === 'resource_missing' && error.message?.includes('test mode')) {
            console.log(`Test mode subscription ID found for ${userEmail}, treating as no subscription`);
            subscriptionStatus = 'test_mode';
          } else {
            console.error("Error fetching subscription details:", error);
            subscriptionStatus = 'error';
          }
        }
      }

      res.json({
        stripeSubscriptionId: user.stripeSubscriptionId,
        stripeCustomerId: user.stripeCustomerId,
        subscriptionStatus: subscriptionStatus,
        hasSubscription: !!user.stripeSubscriptionId
      });
    } catch (error) {
      console.error("Error fetching subscription status:", error);
      res.status(500).json({ message: "Failed to fetch subscription status" });
    }
  });

  // Update user fund in CryptoBot API (admin only)
  app.post('/api/admin/users/:userId/update-fund', isAdminAuthenticated, async (req, res) => {
    try {
      const { userId } = req.params;
      const { newFund } = req.body;
      const session = req.session as any;

      // Since we're now using emails as userIds from CryptoBot API, 
      // userId should be the email address
      const userEmail = userId;

      if (!userEmail) {
        return res.status(400).json({ message: "User email not provided" });
      }

      if (!newFund || isNaN(newFund)) {
        return res.status(400).json({ message: "Valid fund amount required" });
      }

      console.log(`Admin updating fund for user: ${userEmail} to ${newFund}`);

      // Call CryptoBot API to update fund
      const fundResponse = await fetch(`https://cryptobot-api-f15f3256ac28.herokuapp.com/account/fund?email=${encodeURIComponent(userEmail)}&new_fund=${newFund}`, {
        method: 'POST',
        headers: {
          'accept': 'application/json',
          'x-api-key': 'L5oQfQ6OAmUQfGhdYsaSEEZqShpJBB2hYQg7nCehH9IzgeEX841EBGkRZp648XDz4Osj6vN0BgXvBRHbi6bqreTviFD7xnnXXV7D2N9nEDWMG25S7x31ve1I2W9pzVhA'
        },
        body: ''
      });

      if (!fundResponse.ok) {
        const errorData = await fundResponse.text();
        console.error('CryptoBot API fund update error:', errorData);
        return res.status(400).json({ 
          message: "Failed to update fund in CryptoBot API" 
        });
      }

      const fundResult = await fundResponse.json();
      console.log('CryptoBot fund update successful:', fundResult);

      // No local database update needed since we use CryptoBot API as source of truth

      // Log admin action
      await storage.logAdminAction({
        adminId: session.adminId.toString(),
        action: 'UPDATE_USER_FUND',
        targetUserId: userEmail,
        details: { 
          userEmail: userEmail,
          newFund: newFund,
          fundResult,
          timestamp: new Date() 
        }
      });

      res.json({ 
        message: "User fund updated successfully",
        fundResult
      });
    } catch (error) {
      console.error("Error updating user fund:", error);
      res.status(500).json({ message: "Failed to update user fund" });
    }
  });

  // Get Dropbox logs link for specific user (admin only)
  app.get('/api/admin/users/:userId/logs-link', isAdminAuthenticated, async (req, res) => {
    try {
      const { userId } = req.params;

      // Since we're now using emails as userIds from CryptoBot API, 
      // userId should be the email address
      const userEmail = userId;

      if (!userEmail) {
        return res.status(400).json({ message: "User email not provided" });
      }

      // Generate Dropbox link using the user's email
      const dropboxLink = `https://www.dropbox.com/home/Apps/cryptobotgemini%20(1)/cryptobotgemini/${encodeURIComponent(userEmail)}`;

      res.json({ 
        dropboxLink,
        userEmail: userEmail,
        userId: userEmail
      });
    } catch (error) {
      console.error("Error generating logs link:", error);
      res.status(500).json({ message: "Failed to generate logs link" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

