                      <TableHead>Email</TableHead>
                      <TableHead>Funds</TableHead>
                      <TableHead>Trading</TableHead>
                      <TableHead>API Keys</TableHead>
                      <TableHead>Subscription</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {users.map((user: any) => (
                      <TableRow key={user.id} data-testid={`row-user-${user.id}`}>
                        <TableCell>
                          <div>
                            <div className="font-medium">{user.firstName} {user.lastName}</div>
                            <div className="text-sm text-gray-500">ID: {user.cryptoBotId || user.id}</div>
                          </div>
                        </TableCell>
                        <TableCell>{user.email}</TableCell>
                        <TableCell>
                          <CryptoBotFundsCell userId={user.id} userEmail={user.email} />
                        </TableCell>
                        <TableCell>
                          <CryptoBotStatusCell userId={user.id} userEmail={user.email} />
                        </TableCell>
                        <TableCell>
                          <Badge variant={user.hasGeminiKeys ? "default" : "destructive"}>
                            {user.hasGeminiKeys ? "Configured" : "Missing"}
