        <DialogContent>
          <DialogHeader>
            <DialogTitle>Update API Credentials</DialogTitle>
            <DialogDescription>
              Update Gemini API credentials for this user
            </DialogDescription>
          </DialogHeader>
          {selectedUser && (
            <Form {...editForm}>
              <form onSubmit={editForm.handleSubmit(onEditSubmit)} className="space-y-4">
                <div className="text-sm text-gray-600">
                  Updating API credentials for: {selectedUser.firstName} {selectedUser.lastName} ({selectedUser.email})
                </div>

                <FormField
                  control={editForm.control}
                  name="geminiApiKey"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Gemini API Key</FormLabel>
                      <FormControl>
                        <Input type="password" placeholder="Enter new API key (optional)" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={editForm.control}
                  name="geminiApiSecret"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Gemini API Secret</FormLabel>
                      <FormControl>
                        <Input type="password" placeholder="Enter new API secret (optional)" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex justify-end space-x-2">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setShowEditDialog(false)}
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={updateUserMutation.isPending}
                  >
                    {updateUserMutation.isPending ? "Updating..." : "Update User"}
                  </Button>
                </div>
              </form>
            </Form>
          )}
        </DialogContent>
      </Dialog>

      {/* Fund Update Dialog */}
      <Dialog open={showFundDialog} onOpenChange={setShowFundDialog}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Update User Fund</DialogTitle>
            <div className="text-sm text-gray-600">
              Update fund amount for: <strong>{selectedUser?.email}</strong>
            </div>
          </DialogHeader>
          {selectedUser && (
            <Form {...fundForm}>
              <form onSubmit={fundForm.handleSubmit(onFundSubmit)} className="space-y-4">
                <FormField
                  control={fundForm.control}
                  name="newFund"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>New Fund Amount (S$)</FormLabel>
                      <FormControl>
                        <Input 
                          type="number" 
                          step="0.01"
                          min="1"
                          placeholder="Enter fund amount" 
                          {...field}
                          onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex justify-end space-x-2">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setShowFundDialog(false)}
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={updateUserFundMutation.isPending}
                  >
                    {updateUserFundMutation.isPending ? "Updating..." : "Update Fund"}
                  </Button>
                </div>
              </form>
            </Form>
          )}
        </DialogContent>
      </Dialog>

      {/* Trading State Dialog */}
      <Dialog open={showTradingDialog} onOpenChange={setShowTradingDialog}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Toggle Trading State</DialogTitle>
            <div className="text-sm text-gray-600">
              Manage trading status for: <strong>{selectedUser?.email}</strong>
            </div>
          </DialogHeader>
          {selectedUser && (
            <div className="space-y-6">
              {/* Current Status Display */}
              <div className="bg-gray-50 p-4 rounded-lg space-y-3">
                <h4 className="font-medium text-gray-900">Current Status</h4>
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Trading State:</span>
                  <CryptoBotStatusCell userId={selectedUser.id} userEmail={selectedUser.email} />
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">CryptoBot ID:</span>
                  <span className="text-sm font-mono text-gray-900">{selectedUser.cryptoBotId || 'N/A'}</span>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="space-y-3">
                <p className="text-sm text-gray-600">
                  Toggle the trading state to activate or deactivate automated trading for this user.
                </p>

                <div className="flex justify-end space-x-2">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setShowTradingDialog(false)}
                  >
                    Cancel
                  </Button>
                  <Button 
                    onClick={() => {
                      toggleTradingMutation.mutate(selectedUser.id);
                      setShowTradingDialog(false);
                    }}
                    disabled={toggleTradingMutation.isPending}
                    className="bg-blue-600 hover:bg-blue-700"
                  >
                    {toggleTradingMutation.isPending ? "Toggling..." : "Toggle Trading State"}
                  </Button>
                </div>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

